module.exports = function(grunt){
    grunt.initConfig({
        handlebarsmin: {
            single: {
               files: {
                    "./min/div.handlebars": "./templates/div.handlebars",
                    "./min/table.handlebars": "./templates/table.template"
                }
            },

            multi: {
                expand: true,
                src: ["./templates/*.*"],
                dest: "./min/",
                ext: ".handlebars"
            },

            multiNoExt: {
                expand: true,
                src: ["./templates/*.*"],
                dest: "./min"
            }
        }
    });

    grunt.loadTasks("../tasks");
    grunt.registerTask("default", ["handlebarsmin:single"]);
};